@misc{unknown-a,
  title = {We want to emphasize that approximately half of the patients in Switzerland were treated for suspected sarcoidosis or other rheumatic diseases. It was via the active case finding mechanisms that these cases were identified. Furthermore, we would like to point out that, since the publication of our report (2), only one case known to the Task Force has been identified in Switzerland (in a patient whose surgery was performed in 2014). This finding could indicate that the formation of the national task force not only increased awareness but also ensured that the remaining infectious risk was reduced to a minimum within a very short time},
  language = {}
}
