@misc{unknown-b,
  title = {The wide spread of infection of 2019-nCoV has arouse an international concern since its original outbreak in Wuhan, China. Scientists and health workers around the world are currently working together to wipe out the virus and the novel coronavirus pneumonia (NCP), which has killed more than a thousand lives, by far, worldwide},
  language = {}
}
@misc{unknown-c,
  title = {With the current epidemic being so severe, it is necessary and urgent to make potentially reasonable recommendations for the treatment or prevention for 2019-nCoV or NCP. The two authors clearly proposed that lithium might be a potential treatment or prophylaxis for 2019-nCoV or NCP based on a summary of existing literature that reported the in vitro effects of lithium on coronaviral infections and discussed potential mechanisms, which sound reasonable to some extent, but still not rigorous},
  language = {}
}
@misc{unknown-d,
  title = {In terms of discussion, the authors reviewed some existing literature and suggested a potential mechanism of reduced apoptosis by lithium, the glycogen synthase kinase 3-beta (GSK-3β) inhibitor. The possibility that targeting at GSK-3β by lithium may potentially affect the coronavirus is an interesting topic. However, direct in vitro evidence is lacking regarding 2019-nCoV or related coronaviruses including severe acute respiratory syndrome (SARS) and Middle East respiratory syndrome (MERS) coronaviruses},
  language = {}
}
@thesis{collectively-a,
  author = {Collectively},
  title = {this study proposes a potential role of lithium in treating or preventing 2019-nCoV or NCP with some possible mechanisms. However, by far, solid evidence is lacking to validate this hypothesis. The time of developing lithium orotate for clinical use, even in emergency, is not yet},
  language = {}
}
