@misc{unknown-a,
  title = {This is an important manuscript identifying potential vaccine targets for 2019-nCoV, using computational prediction. In the absence of patient samples, such approaches are instrumental to guide towards quick and efficient identification of vaccine candidates. The authors used 10 open reading frame sequences of 2019-nCoV deposited at NCBI and conducted sequence alignment against immunogenic peptides deposited in the Immune Epitope Database and Analysis Resource (IEDB) database. They identified 28 peptides with sequences matching exactly to severe acute respiratory syndrome-related coronavirus (SARS CoV), that have previously been characterised as immunogenic by T cell assays. These findings are very promising and have the added benefit of potentially developing a vaccine against both SARS and COVID-19},
  language = {}
}
