@incollection{unknown1995a,
  title = {The understanding that every individual is at risk of developing disease and should take specific preventive measures has only recently come to the foreground of medical research and healthcare. Social psychologist JohnArne Skolbekken},
  date = {1995},
  source = {has demonstrated what he calls a “risk epidemic” in medical research. While in 1967-1971 the word “risk” occurred in the title or abstract of 0,1% of medical research papers indexed in MEDLINE, in 1987-1991 the same word occurred in 4,5% of all papers. For the leading generalist journal New England Journal of Medicine the corresponding figure rose from 0,2% to 10,3%. Thus, the concept of risk has come to play a central role in medical thinking. Epidemiological studies provide statistical evidence of an ever-increasing number of risks to health, and preventive healthcare expands by incorporating many of these risks into its field of vision, seeking to identify risk in individuals and eliminating it},
  language = {}
}
@incollection{unknown2003a,
  title = {As shown above with the examples of breast cancer and cardiovascular disease, the full implementation of screening programmes requires a tremendous mobilization among the population and in the healthcare services. Actually, so many screening procedures and preventive programmes are available and so many individuals are eligible for preventive treatment that prevention threatens to outrun available healthcare resources. Thus in 2003, it was estimated that to comply with the recommendations of the US Prevention Services Task Force, general practitioners in the US should spend 7,4 hours each day managing risk},
  publisher = {Yarnall et al},
  date = {2003},
  source = {In other words, the most enthusiastic approaches to screening and individually oriented prevention are not sustainable},
  language = {}
}
@incollection{unknown2003b,
  title = {In recent years great expectations have been mobilized that human genetics will provide preventive medicine with new and powerful tools. While these expectations received maximum publicity at the time of the Human Genome Project at the turn of the century, policymakers and healthcare authorities still seem to hold strong hopes that research in human genetics will translate into improved preventive healthcare. The idea of individualizing prevention based on genetic information is central to these expectations, as is seen in this quote from the White paper on genetics published by the Government of the United Kingdom in 2003, with the title Our Inheritance, Our Future –},
  booktitle = {Realizing the potential of genetics in the NHS: “Above all, genetics holds out the},
  date = {2003},
  source = {promise of more personalized healthcare with prevention and treatment tailored according to a person’s individual genetic profile”(Department of Health},
  language = {}
}
