@article{unknown2009d,
  title = {If this third line of thinking is the right one, as I think it is, why do we not act on it? In a well reasoned analysis, William E},
  journal = {Rees},
  date = {2009},
  source = {argues that mainstream economic thinking compounds a fundamental biological characteristic. All species, including humans, expand to occupy all accessible habitats and to use all available resources unless or until constrained by negative feedback. In the competition for habitat and resources, evolution favours individuals who are most adept at satisfying short-term needs even when negative consequences down the road are obvious. In other words, a tendency to discount the future has evolved by natural selection. Rees argues that the consequences of that biological trait are then worsened by the idea of continuous economic growth, an idea that became important to economic thinking only in the middle of the past century},
  language = {}
}
