@book{unknown2008a,
  title = {I am neither political scientist nor economist, but even a simple physician can observe some of the mechanisms of institutional paralysis. For what it’s worth, here’s what I have observed. In my country, Denmark, agriculture, livestock production in particular, accounts for an important part of the country’s emissions of greenhouse gases. The Danish government’s Ministry of Food, Agriculture and Fisheries therefore commissioned a university report on agriculture and climate change, but it required that recommendations were not to include changes in the organization of Danish agriculture or in the production of food, most of which is exported. The university report therefore explicitly excluded a reduction in livestock production from its recommendations},
  publisher = {Olesen et al},
  date = {2008},
  language = {}
}
@misc{unknown-h,
  title = {The three institutional players here – government, university, and industry – are closely related. The current Minister, for example, with responsibility for regulating agriculture and the food industry, was until 2007 vice-chairman of the organization that represents agricultural and industrial interests (the Danish Agriculture & Food Council). The ministry is also an important source of funding for the departments at the University of Copenhagen and the University of Aarhus that produced the report on climate change and agriculture. So, at least in Denmark, close relationships between government, industry and university ruled out the most obvious way to reduce emissions of greenhouse gases from agriculture: producing fewer cows and pigs. Relationships of this sort characterize the corporate state. That’s not always bad, but in this and many other cases, the resultant of the forces involved has been paralysis of a process that, by any measure, could have been good governance},
  language = {}
}
