@misc{unknown-p,
  title = {Introduction In August of 2010 the Nordic Committee on Bioethics held a conference, in Reykjavik, on ethical issues in public health. Life style, as well as common diseases and other health issues, was on the agenda. In the conference program the following question was posed: „What are the most efficient and fair ways to deal with these problems?” The fact that this question seems anodyne to the average reader betrays the fact that not only the general public, but health professionals and other scholars as well presume that efficiency and fairness – stand-ins for economics and ethics – are, as the question implies, separate phenomena, or perhaps even opposites},
  language = {}
}
@misc{unknown-q,
  title = {Thus utilitarians represent a variety of consequentialists, in that they gauge the moral worth of an action in terms of its outcome: the good that it generates minus the bad that it generates. The fact that Jeremy Bentham (1748–1832), John Stuart Mill (1806–1873), and Adam Smith (1723– 1790) could all be considered both utilitarian philosophers and economists is due to the fact that all three evaluated the moral value of policies affecting well-being in terms of their utility. Of course, economics is not a philosophy but an intellectual structure that permits various forms of robust analysis. The methods provide a positive descriptive detection, that – despite its positive nature – can including the semi-normative phenomena; and while normative analysis is an infrequent guest in the realm of academic economics, when called upon it often proves to be quite useful in helping its hosts to determine whether, in a given situation, one is getting more for less or less for more},
  language = {}
}
@book{unknown1978a,
  title = {At the same time as the concerns for life-style associated diseases increase, individual freedom to choose and lead your life according to your own plan has become ever more valued and emphasized in the northwestern part of the world. Correspondingly, as our knowledge about health effects and our possibilities to influence our own health (e.g. in nearby health studios) has increased significantly, the criteria for a healthy life become ever more ideal and distant. This raises the question of whether there is a right to live an unhealthy life and whether we have a responsibility for our life style choices with respect to health care},
