@misc{unknown2009b,
  title = {In somatic health care, rationing on the supply side on the basis of medical benefits or efficacy alone would have to start with those services that are provided for social reasons. For example, the abortion of a healthy fetus usually has no medical benefit; in this context, rationing would of necessity apply to most procedures. Risto Pelkonen, the Archiatre of Finland, has stated in public discussion that, in principle, abortion is “contrary to a doctor’s ethics,” and fetal screening leading to selective abortion is problematic, especially from the perspective of the disabled, because their lives are evaluated as being less worthwhile (Pelkonen’s comments are quoted in Härkönen 2010; for extensive research into ethical problems in Finnish fetal screening practices, see Pruuki 2007 and Meskus},
  date = {2009},
  language = {}
}
@misc{unknown-d,
  title = {The concept of rationing is a difficult one, as it invokes two notions that are related, but distinct, to borrow Carl Cohen’s distinction (Cohen 1985). The first notion is that we to tend assume that there are equitable restrictions at the micro-level, so that a person does not get too much more than his (fixed) share. There is also a macro-level view that assumes there is intelligent deployment taking place, so that greater societal goals are advanced. In this regard the general understanding is that finite resources, such as in health care, must be rationed (the basic meaning of rationing is to withhold some beneficial good or service because of its limited supply). However, based on Cohen, one cannot fully understand the issue of rationing without invoking the idea of reasonableness in the sense of using ratio in its Latin meaning of “to reason,” i.e., to plan ahead coherently and with forethought},
  language = {}
}
@misc{unknown-e,
  title = {In practice this means that since good health is not apportioned equally among all citizens, it is not necessary to deliver health care in the same manner, in the same fixed quantity for all. It would be more important to find those patients and groups able to benefit from treatment. In most cases the need for health care is finite within a society, just as the population is finite. Thus, a certain portion of the population that can benefit from treatment should be identified. For example, it is possible to determine who needs cataract surgery or hip replacement operations by using epidemiological studies and to allocate resources that specifically target those groups},
  language = {}
}
