@misc{unknown-b,
  title = {To improve the diversity of products and generate better antibody drugs, major development efforts have yielded models, such as the fully human antibody mouse and second-generation chimeric human antibody mice, over the 30 years since the first transgenic mouse was generated in 1989 [167]. The continued refinement and advancement of transgenic animals provides ever more possibilities for antibody drug development by global pharmaceutical factories},
  language = {}
}
@misc{unknown-c,
  title = {Different influenza viruses cause epidemics ever year, and influenza vaccines are the most useful measure to prevent seasonal influenza. Single B cell isolation for the generation of potent and broadly neutralizing antiinfluenza antibodies has become a popular undertaking [234, 235]. MHAA4549A, a human mAb targeting the hemagglutinin stalk of influenza A virus was cloned from a single human plasmablast cell from an influenza virus vaccinated donor [236]. A Phase II clinical trial of MHAA4549A as a monotherapy for acute uncomplicated seasonal influenza A in otherwise healthy adults was recently completed (NCT02623322). CT P27, which contains two human mAbs (CT P22 and CT P23), was created by Celltrion and is in Phase II (NCT03511066). RG 6024, also named MHAB5553A, was generated by Genentech with a modified version of the single B cell isolation method [237]; it is currently under examination in Phase I (NCT02528903) trial. The Phase II trial for another antibody, TCN 032, was stopped in 2012 (NCT01719874)},
  language = {}
}
@misc{unknown-d,
  title = {As a result of highly active development of antibody drugs in recent decades, mAbs have emerged among the major class of therapeutic agents for the treatment of many human diseases, especially cancers, immunological, infectious, neural and metabolic diseases. Sales growth and regulatory approval of mAb products were slow until the late 1990s when the first chimeric mAbs were approved (annual sales of $0.3 billion in 1997). With the subsequent approval of humanized and then fully human mAbs, the rate of product approvals and sales of mAb products has increased rapidly, with global sales revenue for all mAb products at $115.2 billion in 2018 (Fig. 1) [276]. The continued growth of mAb products in the coming years is expected to be a major driver of overall biopharmaceutical product sales},
  language = {}
}
