@misc{unknown-a,
  title = {SARS-CoV-2 started in December 2019. On the 30 January 2020, the World Health Organization declared this event a Public Health Emergency of International Concern. The reported cases and deaths of COVID-19 already exceed those of SARS or MERS. Here we highlight some of the key recent findings related to this global epidemic},
  language = {}
}
@misc{unknown-b,
  title = {found in bats3,4. Recombinations between coronaviruses are common, and SARS-CoV is believed to be a recombinant between bat sarbecorviruses. Interestingly, the whole genome of SARS-CoV-2 is highly similar to that of a bat coronavirus detected in 2013 (>96% sequence identity)4, which suggests that the immediate ancestor of SARS-CoV-2 has been circulating in bats for at least several years},
  language = {}
}
@misc{unknown-c,
  title = {Full genome analyses of the virus2,3 indicate that this epidemic was caused by a single zoonotic introduction and that the virus is relatively stable, genetically, in humans3. The first human cluster was reported in association with exposure to a seafood market2,5 that is known to sell live wild game animals for consumption. It is possible that the zoonotic transmission of SARS-CoV-2 might involve an intermediate host (or hosts), as was observed in the SARS epidemic. However, some of the earliest cases had no epidemiological exposure to this market5. It is therefore not yet clear whether the initial zoonotic jump occurred directly from bats to humans or whether an intermediate mammalian species was involved. Identification of the antecedent zoonotic source is relevant because further zoonotic transmission events may well occur unless the transmission pathways of the initial zoonotic event are identified and interrupted},
  language = {}
}
@misc{unknown2019a,
  title = {Using data from the numbers of exported cases from Wuhan and data on travel patterns, Wu et al. estimated that there were >75,000 infected people in Wuhan between 1},
  date = {2019-01-25},
  language = {}
}
@misc{unknown-d,
  title = {With the current trends and assuming a reduction in transmissibility due to interventions, they predicted the outbreak in Wuhan will peak in April 2020. They also predicted that this epidemic will continue to grow exponentially outside Wuhan. Their simulations further suggested that a 50% reduction in the transmission of this disease achieved through publichealth interventions, but without a reduction in population movement, can dramatically delay the exponential growth of this disease for at least a few months. While implementation of aggressive disease-control measures such as school closure and social distancing may defer the establishment of transmission in countries at risk of disease importation, it is still unclear if global spread of this disease can now be prevented},
  language = {}
}
@book{unknown-e,
  title = {Although much has been learned in the past few weeks, a number of crucial knowledge gaps remain. These include the modes of transmission, the stability of the virus in environments, mechanisms of pathogenesis and effective treatments and vaccines. In the current circumstances, the most important question is that of disease severity. It is relevant to note that in the early stage of the 2009 H1N1 influenza virus pandemic, case-fatality estimates as high as 10% were reported. However, populationbased age-stratified sero-epidemiological studies revealed that the true overall case fatality was about 0.001%},
  publisher = {ref},
  language = {}
}
