@misc{unknown-b,
  title = {The study by Abbott and colleagues is one of several 2019-nCoV modelling studies that have appeared during the last two weeks and describes the early transmission dynamics during the outbreak in Wuhan, China. Using a stochastic branching process model, the authors find that the observed outbreak is most compatible with a large initial transmission event, a SARS-like serial interval, and an R0 between 2 and 4. What I particularly like about the study - and what distinguishes it from some of the other modelling papers - is the addition of reporting delays to the simulated case counts},
  language = {}
}
@misc{unknown-c,
  title = {While the modelling approach is sound, a couple of things about the simulation procedure remain somewhat unclear: 1. The authors write that "The start date was determined by combining the duration of the transmission event with the date the fish market in Wuhan, the source of the outbreak, closed (31st of December 2019)." How exactly was the start date determined? The start date is actually one of the most crucial aspects of the analysis, and it is not reported in the text},
  language = {}
}
@article{unknown-d,
  title = {The authors write that they either use a parametric description of the reporting delay or sample from the empirical delays. Does this mean that they use different delays at different times during the outbreak? Overall, I think that the study would gain from discussing the recent literature on the outbreak dynamics and findings from phylogenetic analyses. In particular, I think the authors should discuss their findings in light of the studies by Riou & Althaus1 (Euro Surveill, PMID: 32019669) and Li et al.2 (N Engl},
  journal = {J Med, PMID},
  volume = {31995857},
  source = {which provided the first published estimates of R0 for 2019-nCoV. It would also be interesting to compare the start date of the simulated outbreaks to preliminary estimates of tMRCA (see http://virological.org, for example). Finally, I think the issue of under reporting and potential biases in using reported case counts deserves some additional discussion},
  language = {}
}
