@36{unknown-b,
  title = {Other Immunosuppressive Agents Plasmapheresis is effective in acute inflammatory conditions. Kawasaki disease is an acute inflammatory disease so plasmapheresis is ideal condition for this intervention. Kawasaki itself is self-limiting disease and plasmaphereses being very complex and hazardous conditions [34], it is suggested to use it in those children who have undergone all the other pharmacologic treatment and still having active vasculitis. In a study [35] which included 10 patients from 2006-2015 with failed IVIG and complicated by coronary artery lesions were treated with plasma exchange. 6 out of 10 patients had their coronary artery lesions at normal level after one of treatment. No stenosis in 9 out of 10 patients. Another study involving 4 patients also concluded plasma exchange be effective for refractory Kawasaki disease},
  medium = {36},
  language = {}
}
